const{getSeed}=require("./index"),testString="Commondev.ru the best it community";test("Checking type of seed",()=>{expect("string"==typeof getSeed(testString)).toBeTruthy()}),test("Checking default length of code",()=>{expect(24===getSeed(testString).length).toBeTruthy()}),test("Checking biger length of code",()=>{expect(getSeed(testString,32).length===32).toBeTruthy()}),test("Checking smaller length of code",()=>{expect(getSeed(testString,8).length===8).toBeTruthy()});